import React, { Component } from "react";
import "../bootstrap.css";
import WeatherIcon from './WeatherIcon';
import "../Preview.css";
import moment from 'moment';
import 'moment/locale/de';
class Preview extends Component {


  render() {
    if (this.props.data !== undefined) {
      if (this.props.data.weather !== undefined) {
        moment.locale("de");
        const {daily} = this.props.data.weather.data;
        return (
          daily.map(forecast => {
            return (
              <div className="preview-wrapper">
                <div className="day-card">
                  <div className="day-card-group-1">
                    <div className="text-secondary">{moment.unix(forecast.dt).format("dddd.")}</div>
                    <WeatherIcon
                      status={forecast.weather[0].main}
                      size="lg"
                    />
                  </div>
                  <div className="day-card-group-2">
                    <span>{forecast.temp.max}°</span>
                    <span className="text-secondary">{forecast.temp.min}°</span>
                  </div>
                </div>
              </div>
            );
          })
        );
      }
      else {
        return (<div>Waiting..</div>);
      }
    }
    else {
      return (<div>Waiting..</div>);
    }
  }
}

export default Preview;
