import React, { Component } from 'react';
import { library } from "@fortawesome/fontawesome-svg-core";
import { faCloudSun, faCloud,faBolt,faWind,faCloudRain,faCloudSunRain,faCloudShowersHeavy,faSnowflake } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
library.add(faCloudSun, faCloud,faBolt,faWind,faCloudRain,faCloudSunRain,faCloudShowersHeavy,faSnowflake );
class WeatherIcon extends Component {
    state = {  }
    iconReference={
        sun: {
            Main:'Clear',
            iconName:'sun',
            class:'sun'
        },
        rain:{
            Main:'Rain',
            iconName:'cloud-rain',
            class:'rain'
        },
        thunderstorm:{
            Main:'Thunderstorm',
            iconName:'bolt',
            class:'thunderstorm'
        },
        cloud:{
            Main:'Clouds',
            iconName:'cloud',
            class:'cloud'
        },
        snow:{
            Main:'Snow',
            iconName:'snowflake',
            class:'snow'
        }
    }
    getIconReference(mainStatus){
        for(icon in iconReference){
            if(iconReference[icon].Main === mainStatus){
                return {
                    iconName: iconReference[icon].iconName,
                    iconClass: iconReference[icon].class
                }
            }
        }
    }
    
    render() { 
       const {iconName, iconClass} = this.getIconReference(this.props.status);
        return ( 
            <div>
                <FontAwesomeIcon
                 icon="snow"
                 icon={iconName}
                 size={this.props.size}
                 className={iconClass}
                />
            </div>
         );
    }
}
 
export default WeatherIcon;
