import React, { Component } from "react";
import "../bootstrap.css";
import "../index.css";
import { Line } from "react-chartjs-2";
import "../Details.css";
import { library } from "@fortawesome/fontawesome-svg-core";
import { faCloudSun, faCloud } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import moment from 'moment';
import 'moment/locale/de';
library.add(faCloud, faCloudSun);

class Details extends Component {
  state = {
    componentHasUpdated: false
  };

  getLineChartData(hourlyData) {
    let filteredData = [];
    let counter = 0;
    for (let i = 0; i < hourlyData.length; i++) {
      if (counter === 3) {
        counter = 0;
      }
      if (counter === 0) {
        filteredData.push(hourlyData[i].temp);
      }
      counter++;
    }
    return {
        datasets: [{
          data: filteredData
        }],
        labels: filteredData
    }
  }
  render() {
    if (this.props.data !== undefined) {
      if (this.props.data.weather !== undefined) {

        const { current } = this.props.data.weather.data;
        const { hourly } = this.props.data.weather.data;
        moment.locale('de');
        console.log(this.props.data);

        return (
          <div className="details-wrapper">
            <div>
              <div style={{ display: "inline", float: "left" }}>
                <div className="info-group-1">
                  <div className="info-group-item" id="main">
                    <FontAwesomeIcon
                      style={{ margin: "0 0.25em" }}
                      icon="cloud"
                      size="sm"
                      className="cloud"
                    />
                    <span id="currentTemperature">{Math.round(current.temp)}</span>
                  </div>

                  <div className="info-group-item" id="config">
                    <span className="temperatureConfig-item">°C</span>
                    <span className="temperatureConfig-item">|</span>
                    <span className="temperatureConfig-item">°F</span>
                  </div>

                  <div className="info-group-item" id="secondary">
                    <span className="text-secondary">Niederschlag: 0%</span>
                    <span className="text-secondary">Luftfeuchte: {current.humidity}%</span>
                    <span className="text-secondary">Wind: {current.wind_speed} km/h</span>
                  </div>
                </div>
              </div>
              <div style={{ display: "inline", float: "right" }}>
                <div className="info-group-2" id="third">
                  <h3>Backnang</h3>
                  <h5 className="text-secondary">{moment().format("dddd HH:mm")}</h5>
                  <h5 className="text-secondary">{current.weather[0].description}</h5>
                </div>
              </div>
            </div>
            <div>
              <Line
                width={100}
                height={100}
                options={{ maintainAspectRatio: false }}
                data={() => { this.getLineChartData(hourly) }}
              />
            </div>
          </div>
        );
      }
      else {
        return (
          <div>Waiting for data</div>
        );
      }
    }
    else {
      return (
        <div>Waiting for data</div>
      );
    }

  }
}

export default Details;
