import React, { Component } from "react";
import "../bootstrap.css";
import "../index.css";
import Details from "./Details";
import Preview from "./Preview";
class Wrapper extends Component {
  constructor(props) {
    super(props);
  }
  componentDidMount() {
    this.getLocation();
    setInterval(this.getWeatherData, 500);
  }
  state = {
    gotLocationData: false,
  };
  data={
    weather: this.state.weather,
    location: this.state.location
  }
  render() {
    return (
      <div className="container center">
        <Details data={this.state}></Details>
        <Preview data={this.state}></Preview>
      </div>
    );
  }
  getLocation = () => {
    navigator.geolocation.getCurrentPosition((position) => {
      var { latitude, longitude } = position.coords;

      this.state.location = {
        latitude,
        longitude,
      };
      this.setState({ gotLocationData: true });
    });
  };
  getWeatherData = async () => {
    if (this.state.gotLocationData == true) {
      var lat = this.state.location.latitude;
      var lon = this.state.location.longitude;
      const key = process.env.REACT_APP_WeatherApiKey;
      const link = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&units=metric&lang=de&appid=${key}`;
      const response = await fetch(link);
      const data = await response.json();
      this.state.weather = {
        data,
      };
      this.setState({ gotLocationData: false });
    }
  };
}

export default Wrapper;
