import React, { Component } from "react";
import "../bootstrap.css";
import "../index.css";
import Details from "./Details";
import Preview from "./Preview";
class Wrapper extends Component {
  constructor(props) {
    super(props);
  }
  async componentDidMount() {
    this.getLocation().then((msg) => {
      console.log(msg);
      this.getWeatherData().then((msg) => {
        console.log(msg);
        console.log(this);
      });
    });
  }
  state = {};
  render() {
    return (
      <div className="container center">
        <Details></Details>
        <Preview></Preview>
      </div>
    );
  }
  async getLocation() {
    navigator.geolocation.getCurrentPosition((position) => {
      var { latitude, longitude } = position.coords;

      this.location = {
        latitude,
        longitude,
      };

      return Promise.resolve("Got current position");
    });
  }
  async getWeatherData() {
    let { latitude, longitude } = this.location;
    const key = process.env.WeatherApiKey;
    const link = `pro.openweathermap.org/data/2.5/forecast/hourly?lat=${latitude}&lon=${longitude}&appid=${key}`;
    const data = await fetch(link);
    this.weather = {
      data,
    };
    Promise.resolve("Got Weather data");
  }
}

export default Wrapper;
